mapper.select:
SELECT *
FROM ATTACHMENT
WHERE BOARD_NO = #{boardNo}

SELECT BOARD_NO,THEME_NO,ZONE_NO
FROM THEME
WHERE BOARD_NO = #{boardNo}

mapper.update:
UPDATE BOARD
SET ZONE_NO = #{zoneNo}
   ,BOARD_TITLE = #{boardTitle}
   ,BOARD_CONTENT = #{boardContent}
WHERE BOARD_NO = #{boardNo}

	UPDATE INFO
	SET ZONE_NO = #{zoneNo}
	   ,INFO_ADDRESS = #{infoAddress} 
 	   ,INFO_TIME = #{infoTime} 
	   ,INFO_HOMEPAGE = #{infoHomepage} 
	   ,INFO_CALL = #{infoCall} 
	   ,DAY_OFF = #{dayOff}
	WHERE BOARD_NO = #{boardNo}

UPDATE THEME
SET THEME_NO = #{themeNo} 
   ,ZONE_NO = #{zoneNo}
WHERE BOARD_NO = #{boardNo}

mapper.select:
SELECT BOARD_NO,ZONE_NO,INFO_NO,TO_CHAR(START_DATE, 'YYYY-MM-DD') START_DATE,TO_CHAR(END_DATE, 'YYYY-MM-DD') END_DATE,FES_CATEGORY,FES_MONEY
FROM FESTIVAL
WHERE BOARD_NO = #{boardNo}

SELECT INFO_NO,BOARD_NO,ZONE_NO,INFO_ADDRESS,INFO_TIME,INFO_HOMEPAGE,INFO_CALL,PARKING,INFO_NAME,INFO_TYPE,DAY_OFF
FROM INFO
WHERE BOARD_NO = #{boardNo}

mapper.update:
	UPDATE INFO
	SET ZONE_NO = #{zoneNo}
	   ,INFO_ADDRESS = #{infoAddress} 
 	   ,INFO_TIME = #{infoTime} 
	   ,INFO_HOMEPAGE = #{infoHomepage} 
	   ,INFO_CALL = #{infoCall} 
	WHERE BOARD_NO = #{boardNo}

UPDATE THEME
SET ZONE_NO = #{zoneNo}
   ,START_DATE = #{startDate}
   ,END_DATE = #{endDate}
   ,FES_CATEGORY = #{fesCategory}
   ,FES_MONEY = #{fesMoney}
WHERE BOARD_NO = #{boardNo}

UPDATE ATTACHMENT
SET ORIGIN_NAME = #{originName},
	CHANGE_NAME = #{changeName},
	FILE_PATH = #{filePath}
WHERE WRITER = #{writer}

UPDATE SERVICE
SET SERVICE_TITLE = #{serviceTitle},
	SERVICE_CONTENT = #{serviceContent}
WHERE SERVICE_NO = #{serviceNo}	
AND STATUS = 'Y'

UPDATE ATTACHMENT
SET ORIGIN_NAME = #{originName},
	CHANGE_NAME = #{changeName}
WHERE BOARD_NO = #{boardNo}	
AND STATUS = 'Y'

UPDATE SERVICE
SET ANSWER_STATUS = 'Y'
WHERE SERVICE_NO = #{refQno}

UPDATE REPLY
SET CONTENT = #{content}
WHERE REPLY_NO = #{replyNo}
AND STATUS = 'Y'
