mapper.select:
SELECT COUNT(*)
FROM GOOD
WHERE BOARD_NO = #{boardNo}
AND WRITER = #{writer}

mapper.delete:
DELETE FROM GOOD
WHERE BOARD_NO = #{boardNo}
AND WRITER = #{writer}

mapper.update:
UPDATE BOARD
SET GOOD = GOOD-1
WHERE BOARD_NO= #{boardNo}

UPDATE BOARD
SET GOOD = GOOD+1
WHERE BOARD_NO= #{boardNo}

