mapper.select:
SELECT FILE_PATH
FROM ATTACHMENT
WHERE BOARD_NO = #{boardNo}

mapper.update:
UPDATE BOARD
SET COUNT = COUNT+1
WHERE BOARD_NO=#{boardNo}

mapper.select:
SELECT COUNT(*)
FROM CHOICE
WHERE BOARD_NO = #{boardNo}
AND WRITER = #{userNo}

mapper.update:
UPDATE BOARD
SET CHOICE = CHOICE+1
WHERE BOARD_NO = #{boardNo}

mapper.delete:
DELETE FROM CHOICE
WHERE BOARD_NO = #{boardNo}
AND WRITER = #{userNo}

mapper.update:
UPDATE BOARD
SET CHOICE = CHOICE-1
WHERE BOARD_NO = #{boardNo}

mapper.select:
SELECT CHOICE
FROM BOARD
WHERE BOARD_NO = #{boardNo}
