mapper.select:
SELECT BOARD_NO, INFO_NO
FROM BOARD
WHERE BOARD_WRITER = #{boardWriter}
AND CATEGORY = 6
AND BOARD_TITLE = #{boardTitle}
AND BOARD_CONTENT = #{boardContent}

SELECT INFO_NO
FROM INFO
WHERE INFO_NAME = #{infoName}

mapper.update:
UPDATE PLAN
SET INFO_NO = INFO_NO||'/'||#{infoNo}
WHERE BOARD_NO = #{boardNo}
