mapper.delete:
  	DELETE FROM REPLY
		WHERE REPLY_NO = #{replyNo}
		AND STATUS = 'Y'

mapper.update:
  	UPDATE REPLY
		SET CONTENT = #{content}
		WHERE REPLY_NO = #{replyNo}
		AND STATUS = 'Y'

mapper.select:
	SELECT COUNT(*) 
	FROM CHOICE
    WHERE BOARD_NO = #{boardNo} 
    AND WRITER = #{writer}

mapper.delete:
  	DELETE FROM CHOICE
		WHERE BOARD_NO = #{boardNo}
		AND WRITER = #{writer}

mapper.select:
  	SELECT BOARD_NO,WRITER
		FROM CHOICE
		WHERE BOARD_NO = #{boardNo}

SELECT BOARD_NO,ZONE_NAME,BOARD_WRITER,BOARD_TITLE,BOARD_CONTENT ,COUNT,FILE_PATH ,INFO_HOMEPAGE,INFO_CALL FROM BOARD B JOIN THEME USING (BOARD_NO) JOIN ZONE Z ON (Z.ZONE_NO = B.ZONE_NO) JOIN ATTACHMENT USING(BOARD_NO) JOIN INFO USING(BOARD_NO) WHERE B.STATUS = 'Y' AND CATEGORY = 1 AND THEME_NO = 3 AND FILE_LEVEL = 1
AND ZONE_NAME = #{zoneName}
ORDER BY COUNT DESC

